mapper.update:
UPDATE cart
SET checked =#{checked},
update_time=now()
where user_id=#{userId}

  and commodity_id =#{commodityId}

mapper.select:
SELECT IFNULL(sum(quantity),0) as count
FROM cart
WHERE user_id=#{userId}
